import React from 'react';
import { bindActionCreators } from 'redux';
import { connect } from "react-redux";
import { setPage, setPagePosts } from '../store/reducers/posts';
import { getPosts } from "../utils/requests";
import axios from "axios";

import LinearProgress from '@material-ui/core/LinearProgress';
import Grid from "@material-ui/core/Grid";
import Post from "./Post";
import {createStyles, makeStyles, Theme} from "@material-ui/core/styles";

const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            margin: '-30px auto 0',
            justifyContent: 'center',
            alignContent: 'center',
            [theme.breakpoints.down('sm')]: {
                marginTop: 0,
            },
        },
        item: {
            margin: 10,
            maxHeight: 250,
            overflow:'hidden',
            minWidth: 200,
            [theme.breakpoints.down('sm')]: {
                maxHeight: 'unset',
            },
        },
        loader: {
            width: '80%',
        },
        pagination: {
            position: 'fixed',
            bottom: 30,
        },
    }),
);


const FactsList = () => {
  const classes = useStyles();
  const [posts, setPosts] = React.useState([]);

  React.useEffect(() => {
    axios.get(`https://cors-anywhere.herokuapp.com/cat-fact.herokuapp.com/facts`)
      .then(response => {
        setPosts(response.data.all);
        console.log(response.data.all);
      });
  }, []);

  return (
    <div>
      <div>{posts.slice(0, 21).map(function (post){
        <div>
          <div>
              <h1>{post.title}</h1>
              <h2>{post.body}</h2>
              <h3>{post.id}</h3>
            <button>Learn More</button>
          </div>
      </div>
      }
      )}
      </div>
    </div>
  );   
}

export default FactsList;

